var dateSplitted = input.split('-');
var ingevuldeDatum = new Date(dateSplitted[2], dateSplitted[1] - 1, dateSplitted[0]);
var verschil = new Date(new Date() - ingevuldeDatum).getFullYear() - 1970;

if((verschil >= 18) && (verschil <= 99)){
  valid = true;
} else{
  valid = 'Ingevulde geboortedatum mag niet jonger dan 18 jaar en ouder dan 99 jaar zijn';
}

