validate.custom:
var eergisteren = new Date((new Date()).valueOf() - 1000*60*60*48);
var invulDatum = new Date(input.split('-')[2] + '-' + input.split('-')[1] + '-' + input.split('-')[0]).getTime();
if(eergisteren >= invulDatum) {
  valid = 'Deze datum mag maximaal 1 dag in het verleden liggen.';
} else {
valid = true;
}
