for (var i = 0; i <= dataGrid.length; i++) {
  if (dataGrid[i] && 
  dataGrid[i].hasOwnProperty('container') && 
  dataGrid[i]['container'].hasOwnProperty(fieldName) && 
  dataGrid[i]['container'][fieldName]) {
    sum = sum + parseFloat(dataGrid[i]['container'][fieldName].replace(',', '.'));
  }
}
