  valueToCheck = row.container.starttijdOpbouw;
} else {
  valueToCheck = row.starttijdOpbouw;
}

if(valueToCheck == '') {
 valid = 'Vul eerst de tijd in wanneer u start met de opbouw van het evenement.';
} else {
  var start = moment(valueToCheck, "HH:mm");
  var totWanneer = moment(input, "HH:mm");
  if(totWanneer >= start) {
