components.customConditional:
show = true;

if(data.geefAanWanneerHetEvenementIs && Array.isArray(data.geefAanWanneerHetEvenementIs)) {

  var opbouwFound = false;
  var eventFound = false;
  var afbouwFound = false;

  for (var i = 0; i < data.geefAanWanneerHetEvenementIs.length; i++) {
      var item = data.geefAanWanneerHetEvenementIs[i];
      var checkmarks = item.container.welkeActiviteitVindenOpDezeDagPlaatsMeerdereAntwoordenMogelijk;

      if(checkmarks.opbouwEvenement) {
        opbouwFound = true;
      }
      if(checkmarks.evenement) {
        eventFound = true;
      }
      if(checkmarks.afbrekenEvenement) {
        afbouwFound = true;
      }
    }


  if(opbouwFound && eventFound && afbouwFound) {
    show = false;
  }
}
