components.customConditional:
show = false;

var isInkomsten = data.inkomsten == 'ja' ? true:false;
var isWonen = data.wonen == 'ja' ? true:false;
var isVakantie = data.vakantieOfTijdelijkInHetBuitenland == 'ja' ? true:false;
var isStudie = data.studie == 'ja' ? true:false;
var isVrijwilligerswerk = data.vrijwilligerswerk == 'ja' ? true:false;
var isVermogen = data.vermogen == 'ja' ? true:false;

if (isInkomsten || isWonen || isVakantie || isStudie || isVrijwilligerswerk || isVermogen){
  show = true;
} else{
  show = false;
}
