show = false;

if ((data.container && data.container.hoeveelSubsidieWiltUOntvangen) && (data.welkeSubsidieWiltUAanvragen)) {
  var subsidie = parseFloat(data.container && data.container.hoeveelSubsidieWiltUOntvangen.replace(',', '.'));
  if ((data.welkeSubsidieWiltUAanvragen == 'b') && subsidie > 4999) {
    show = true;
  }
}

if ((data.container && data.container.hoeveelSubsidieWiltUOntvangen) && (data.watWiltUAanvragen)) {
  var isSubsidie = parseFloat(data.container && data.container.hoeveelSubsidieWiltUOntvangen.replace(',', '.'));
  if ((data.watWiltUAanvragen == 'c') && isSubsidie > 4999) {
    show = true;
  }
}

show = false;

if ((data.container && data.container.hoeveelSubsidieWiltUOntvangen) && (data.welkeSubsidieWiltUAanvragen || data.watWiltUAanvragen)) {
  var subsidie = parseFloat(data.container && data.container.hoeveelSubsidieWiltUOntvangen.replace(',', '.'));
  if (((data.welkeSubsidieWiltUAanvragen == 'd') || (data.watWiltUAanvragen == 'b')) && subsidie < 5000) {
    show = true;
  }
}

if ((data.container && data.container.hoeveelSubsidieWiltUOntvangen) && (data.welkeSubsidieWiltUAanvragen || data.watWiltUAanvragen)) {
  var isSubsidie = parseFloat(data.container && data.container.hoeveelSubsidieWiltUOntvangen.replace(',', '.'));
  if (((data.welkeSubsidieWiltUAanvragen == 'd') || (data.watWiltUAanvragen == 'b')) && isSubsidie > 20000) {
    show = true;
  }
}

show = false;

var isSubsidieC = data.welkeSubsidieWiltUAanvragen == 'c' ? true:false;
var isSubsidieD = data.welkeSubsidieWiltUAanvragen == 'd' ? true:false;
var isAanvraagA = data.watWiltUAanvragen == 'a' ? true:false;
var isAanvraagB = data.watWiltUAanvragen == 'b' ? true:false;

if (isSubsidieC || isSubsidieD || isAanvraagA || isAanvraagB) {
  show = true;
} else {
  show = false;
}
