components.customConditional:
show = false;
if(data.hoeveelSubsidieVraagtUAan) {
  var value = parseInt(data.hoeveelSubsidieVraagtUAan);
  if(value > 5000) {
    show = true;
  }
}

components.calculateValue:
var inkomsten = 0;
var kosten = 0;

if(data.beschrijfDeInkomsten) {
  data.beschrijfDeInkomsten.forEach(function(item, index) {
    if(item.container.inkomstenInEuros) {
      var valueInkomstenItem = parseFloat(item.container.inkomstenInEuros.replace(',', '.'));
      inkomsten += valueInkomstenItem; 
    }
  })
}
if(data.beschrijfDeKosten ) {
  data.beschrijfDeKosten.forEach(function(item, index) {
    if(item.container.kostenInEuros) {
      var valueKostenItem = parseFloat(item.container.kostenInEuros.replace(',', '.'));
      kosten += valueKostenItem; 
    }
  })
}

var calculated = kosten - inkomsten;
value = calculated.toString().replace('.', ',');
