validate.custom:
var today = new Date();

var dateSplitted = input.split('-');
var ingevuldeDatum = new Date(dateSplitted[2], dateSplitted[1] - 1, dateSplitted[0]);

if(ingevuldeDatum >= today ){
  valid = true;
} else{
  valid = 'Datum mag alleen in de toekomst liggen';
}

components.calculateValue:
var inkomsten = 0;
var kosten = 0;

if(data.lijstInkomsten) {
  data.lijstInkomsten.forEach(function(item, index) {
    if(item.container.inkomstenInEuros) {
      var valueInkomstenItem = parseFloat(item.container.inkomstenInEuros.replace(',', '.'));
      inkomsten += valueInkomstenItem; 
    }
  })
}
if(data.lijstKosten ) {
  data.lijstKosten.forEach(function(item, index) {
    if(item.container1.kostenInEuros) {
      var valueKostenItem = parseFloat(item.container1.kostenInEuros.replace(',', '.'));
      kosten += valueKostenItem; 
    }
  })
}

var calculated = kosten - inkomsten;
value = calculated.toString().replace('.', ',');

