components.customConditional:
show = true;

if(data.inlogmiddel) {
  if(data.inlogmiddel == 'digid') {
    if (data.gemeente == 'Nijmegen'){
      var dateSplitted = data.geboortedatum.split('-');
      var geboortedatum  = new Date(dateSplitted[2], dateSplitted[1] - 1, dateSplitted[0]);
      var verschil = new Date(new Date() - geboortedatum).getFullYear() - 1970;

      if(verschil < 15) {
          show = true;
      }

    } else {
        show = true;
    }
  } 
}

validate.custom:
var today = new Date();
var toekomst = new Date();
toekomst.setDate(today.getDate() + 0);

var dateSplitted = input.split('-');
var ingevuldeDatum = new Date(dateSplitted[2], dateSplitted[1] - 1, dateSplitted[0]);

if(ingevuldeDatum <= toekomst ){
  valid = true;
} else{
  valid = 'Ingevulde geboortedatum mag niet in de toekomst liggen';
}
