components.calculateValue:
value = '';
if(data.geenBelasting && data.geenBelasting.digiD && data.geenBelasting.digiD.voorWie) {
  if(data.geenBelasting.digiD.voorWie == 'mijzelf') {
    value = 'nee';
  } else if(data.geenBelasting.digiD.voorWie == 'iemandAnders') {
    value = 'ja'
  } else if(data.geenBelasting.digiD.voorWie == 'org') {
    value = 'ja'
  }
} else if(data.geenBelasting && data.geenBelasting.eHerkenning && data.geenBelasting.eHerkenning.voorWie1) {
    if(data.geenBelasting.eHerkenning.voorWie1 == 'mijnBedrijfOfOrganisatie') {
    value = 'nee';
  } else if(data.geenBelasting.eHerkenning.voorWie1 == 'iemandAndersOrg') {
    value = 'ja'
  } else if(data.geenBelasting.eHerkenning.voorWie1 == 'andereOrg') {
    value = 'ja'
  }
}
